(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[44990],{44990:n=>{n.exports='<tui-textfield\n    iconEnd="@tui.settings"\n    iconStart="@tui.search"\n>\n    <label tuiLabel>I am a label</label>\n    <input\n        placeholder="I am placeholder"\n        tuiTextfield\n        [(ngModel)]="value"\n    />\n    <tui-icon icon="@tui.bell" />\n    <tui-icon tuiTooltip="I am a hint" />\n</tui-textfield>\n\n<label tuiLabel>\n    For long labels it is better to use label outside as it can wrap to multiple lines\n    <tui-textfield\n        #m\n        iconEnd="@tui.settings"\n        iconStart="@tui.search"\n        tuiTextfieldSize="m"\n    >\n        <input\n            tuiTextfield\n            [placeholder]="m.focused() ? \'I am placeholder\' : \'I am a label\'"\n            [(ngModel)]="value"\n        />\n        <tui-icon icon="@tui.bell" />\n        <tui-icon tuiTooltip="I am a hint" />\n    </tui-textfield>\n</label>\n\n<tui-textfield\n    #s\n    iconEnd="@tui.settings"\n    iconStart="@tui.search"\n    tuiTextfieldSize="s"\n>\n    <input\n        tuiTextfield\n        [placeholder]="s.focused() ? \'I am placeholder\' : \'I am a label\'"\n        [(ngModel)]="value"\n    />\n    <tui-icon icon="@tui.bell" />\n    <tui-icon tuiTooltip="I am a hint" />\n</tui-textfield>\n'}}]);