(self.webpackChunk_taiga_ui_components=self.webpackChunk_taiga_ui_components||[]).push([[21885],{21885:t=>{t.exports='<div [style.margin-bottom.rem]="2">\n    <tui-radio-list\n        [itemContent]="content"\n        [items]="sizes"\n        [style.flex-direction]="\'row\'"\n        [style.width]="\'max-content\'"\n        [(ngModel)]="size"\n    ></tui-radio-list>\n    <ng-template\n        #content\n        let-value\n    >\n        {{ value === \'s\' ? \'Small\' : value === \'m\' ? \'Medium\' : \'Large\' }}\n    </ng-template>\n</div>\n\n<table\n    tuiTable\n    [size]="size"\n    [style.width.rem]="58"\n>\n    <thead>\n        <tr>\n            <th tuiTh>\n                <div [tuiCell]="size">\n                    <input\n                        tuiCheckbox\n                        type="checkbox"\n                        [ngModel]="checked"\n                        [size]="size === \'l\' ? \'m\' : \'s\'"\n                        (ngModelChange)="onCheck($event)"\n                    />\n                    <span tuiTitle>Checkbox</span>\n                </div>\n            </th>\n            <th tuiTh>Title</th>\n            <th tuiTh>Cell</th>\n            <th tuiTh>Status</th>\n            <th\n                tuiTh\n                [style.width.rem]="10"\n            >\n                Items\n            </th>\n            <th tuiTh>Progress</th>\n            <th tuiTh>Actions</th>\n        </tr>\n    </thead>\n    <tbody tuiTbody>\n        <tr *ngFor="let item of data">\n            <td tuiTd>\n                <div [tuiCell]="size">\n                    <input\n                        tuiCheckbox\n                        type="checkbox"\n                        [size]="size === \'l\' ? \'m\' : \'s\'"\n                        [(ngModel)]="item.selected"\n                    />\n                    <span tuiTitle>\n                        {{ item.checkbox.title }}\n                        <span tuiSubtitle>{{ item.checkbox.subtitle }}</span>\n                    </span>\n                </div>\n            </td>\n            <td tuiTd>\n                <div [tuiCell]="size">\n                    <span tuiTitle>\n                        <span tuiStatus>\n                            <tui-icon [icon]="item.title.icon"></tui-icon>\n                            {{ item.title.title }}\n                            <tui-badge\n                                *ngIf="item.title.chip && item.title.subtitle"\n                                size="xs"\n                                [value]="item.title.chip"\n                            ></tui-badge>\n                        </span>\n                        <tui-badge\n                            *ngIf="!item.title.subtitle && item.title.chip; else subtitle"\n                            size="xs"\n                            [value]="item.title.chip"\n                        ></tui-badge>\n                        <ng-template #subtitle>\n                            <span tuiSubtitle>\n                                {{ item.title.subtitle }}\n                            </span>\n                        </ng-template>\n                    </span>\n                </div>\n            </td>\n            <td tuiTd>\n                <div [tuiCell]="size">\n                    <tui-avatar\n                        [src]="item.cell.name | tuiInitials"\n                        [style.background]="item.cell.name | tuiAutoColor"\n                    ></tui-avatar>\n                    <span tuiTitle>\n                        {{ item.cell.name }}\n                        <span tuiSubtitle>{{ item.cell.email }}</span>\n                    </span>\n                </div>\n            </td>\n            <td tuiTd>\n                <span [tuiStatus]="item.status.color">{{ item.status.value }}</span>\n            </td>\n            <td tuiTd>\n                <tui-items-with-more>\n                    <ng-container *ngFor="let chip of item.items">\n                        <tui-badge\n                            *tuiItem\n                            size="m"\n                            [value]="chip"\n                        ></tui-badge>\n                    </ng-container>\n                    <ng-template\n                        let-number\n                        tuiMore\n                    >\n                        <button\n                            tuiAppearance="icon"\n                            tuiLink\n                            [pseudo]="true"\n                        >\n                            + {{ item.items.length - number - 1 }}\n                        </button>\n                    </ng-template>\n                </tui-items-with-more>\n            </td>\n            <td tuiTd>\n                <span tuiStatus>\n                    <progress\n                        size="s"\n                        tuiProgressBar\n                        [style.width.rem]="6"\n                        [value]="item.progress / 100"\n                    ></progress>\n                    {{ item.progress }}ms\n                </span>\n            </td>\n            <td tuiTd>\n                <span tuiStatus>\n                    <button\n                        appearance="link"\n                        icon="tuiIconEdit2"\n                        size="xs"\n                        tuiAppearance="link"\n                        tuiIconButton\n                    >\n                        Edit\n                    </button>\n                    <button\n                        appearance="link"\n                        icon="tuiIconMoreHorizontal"\n                        size="xs"\n                        tuiAppearance="link"\n                        tuiIconButton\n                    >\n                        More\n                    </button>\n                </span>\n            </td>\n        </tr>\n    </tbody>\n</table>\n'}}]);